sp_widget.client_script:
api.controller=function($location) {
  /* widget controller */
  var c = this;
	c.redirect = function(url) {
		console.log("Redirect to " + url);
	$location.url(url);
};
};

sp_widget.css:
//Lazy hides for POC
.sp-attachment-add {
  display : none !important;
}

.action-btn[gsft_id="42df02e20a0a0b340080e61b551f2909"] {
    display : none !important;
}

sp_widget.link:
function link(scope, element, attrs, controller) {
  scope.$on('spModel.uiActionComplete', function(event, response) {
	scope.c.server.update().then(function(response) {
		if (response.redirectURL) controller.redirect(response.redirectURL);
	});
});
}

sp_widget.script:
(function() {
// see https://www.womennow.sn/posts/ui-action-portal-redirect
data.options = {
	view: 'default',
	hideRelatedLists : true
};

data.widget = $sp.getWidget('widget-form', data.options);

	var table = 'x_43553_faceoff_faceoff_instance';
	var currentUser = gs.getUser();
	//data.widget.data.table
	var redirectURL = currentUser.getPreference(table + '.redirectURL');
	if (redirectURL) {
		data.redirectURL = redirectURL;
		// clear user preference
		currentUser.savePreference(table + '.redirectURL','');
	}

})();
