sp_widget.client_script:
api.controller=function($location) {
  /* widget controller */
  var c = this;

};

sp_widget.link:
function link(scope, element, attrs, controller) {
  scope.$on('spModel.uiActionComplete', function(event, response) {
	scope.c.server.update().then(function(response) {
		if (response.redirectURL) controller.redirect(response.redirectURL);
	});
});
}

sp_widget.script:
(function($sp, input, data, options, gs) {

var lea
	if (input) {
		data.sys_id = input.sys_id;
	} else {
		data.sys_id = $sp.getParameter('sys_id');
	}

	var competitorsSysIdArray = [];
	var relatedListQry = "RLQUERYx_43553_faceoff_opponent_criterion.opponent,>=1^criterion="+
			data.sys_id+
			"^ENDRLQUERY";
  // Get my competitors who have the the current pages criterion                     
	var opponentsWithCriterionGr = new GlideRecord('x_43553_faceoff_opponent')
	opponentsWithCriterionGr.addEncodedQuery(relatedListQry);
	var joinQry = opponentsWithCriterionGr.addJoinQuery(
		'x_43553_faceoff_opponent_judgement_eligibility',
		'sys_id',
		'opponent'
	);
	joinQry.addCondition('judge',FaceOffUtils.GetCurrentJudgeSysId());
	opponentsWithCriterionGr.query();

	while(opponentsWithCriterionGr.next()) {
		competitorsSysIdArray.push(opponentsWithCriterionGr.getValue('sys_id'));
	}

  options.table = "x_43553_faceoff_opponent_judgement_eligibility";
	options.fields =  "opponent.name,opponent.description,opponent.avatar,rank,judge"
	options.o= 'rank';
	options.d= '';
	options.filter="opponent.sys_idIN" + competitorsSysIdArray + "^judge="+FaceOffUtils.GetCurrentJudgeSysId();
	options.window_size=20;
	options.view = 'default';
	options.useInstanceTitle = true; // to make sure Data Table widget uses headerTitle always
	//options.headerTitle = "Rankings";
	options.show_breadcrumbs=false;

data.widget = $sp.getWidget('widget-data-table', options);

})($sp, input, data, options, gs);
