sp_widget.client_script:
api.controller=function($location,spAriaFocusManager,$scope) {
  /* widget controller */
  var c = this;
	// Go to leaderboard record
		$scope.$on('data_table.click', function(e, parms){
			var s = {
				id: "leaderboard", 
				table: "x_43553_faceoff_faceoff_leaderboard", 
				sys_id: parms.record.leaderboard.value, 
				view: 'sp'
			};
			console.log('parms:');
			console.log(parms);
			var newURL = $location.search(s);
			spAriaFocusManager.navigateToLink(newURL.url());
	});

};

sp_widget.script:
(function($sp, input, data, options, gs) {

	var tableName;
	if (input) {
		data.sys_id = input.sys_id;
		data.table = input.table;
	} else {
		data.sys_id = $sp.getParameter('sys_id');
		data.table = $sp.getParameter('table');
	}

	var filter = "judge=" + FaceOffUtils.GetCurrentJudgeSysId() +"^my_total_count>=2";
	//var filter = "my_total_count>=2";
	if (data.table == 'x_43553_faceoff_faceoff_leaderboard') {
		filter = filter + "^leaderboard=" +data.sys_id;
	} 

  options.table = "x_43553_faceoff_leaderboard_completion";
	//options.fields =  "rank,opponent.name,opponent.avatar,elo"
	options.o= 'completion_rate';
	options.d= 'completion_rate';
	options.filter=filter;
	options.window_size=20;
	options.view = 'default';
	options.useInstanceTitle = true; // to make sure Data Table widget uses headerTitle always
	options.headerTitle = "My completion statistics";
	options.show_breadcrumbs=false;
	options.filterACLs = true;
	options.page_id = 'fo-form';

data.widget = $sp.getWidget('widget-data-table', options);

})($sp, input, data, options, gs);
