sys_variable_value.value:
(function execute(inputs, outputs) {
    const games = JSON.parse(inputs.response);
    const appidArray = games.response.games
        .filter(game => game.playtime_forever > 0)
        .map(game => game.appid);

    outputs.appids = appidArray;
})(inputs, outputs);

