sys_variable_value.value:
(function execute(inputs, outputs) {
    var game = JSON.parse(inputs.response);
    var tags = game.tags;
    var resultArray = [];

    for (var key in tags) {
        if (tags.hasOwnProperty(key)) {
            resultArray.push({
                name: key,
                weight: tags[key]
            });
        }
    }
    outputs.tags = resultArray;

})(inputs, outputs);

