sys_variable_value.value:
(function execute(inputs, outputs) {
// Define constants
const K = parseInt(inputs.kvalue); // K-factor, determines how much the rating can change after a game

// Calculate the expected score for a player
function getExpectedScore(playerRating, opponentRating) {
    return 1 / (1 + Math.pow(10, (opponentRating - playerRating) / 400));
}

// Update the rating of a player
function updateRating(playerRating, opponentRating, score) {
    const expectedScore = getExpectedScore(playerRating, opponentRating);
    return playerRating + K * (score - expectedScore);
}

// Elo Rating Algorithm
function calculateEloRatings(player1Rating, player2Rating, result) {
    // result should be 1 if player1 wins, 0.5 if it's a draw, and 0 if player1 loses

    const newPlayer1Rating = updateRating(player1Rating, player2Rating, result);
    const newPlayer2Rating = updateRating(player2Rating, player1Rating, 1 - result);

    return {
        player1: Math.round(newPlayer1Rating),
        player2: Math.round(newPlayer2Rating)
    };
}

const player1Rating = parseInt(inputs.elo1);
const player2Rating = parseInt(inputs.elo2);

// Player 1 wins
const result = calculateEloRatings(player1Rating, player2Rating, parseInt(inputs.winner));

outputs.newelo1 = result.player1;
outputs.newelo2 = result.player2;

})(inputs, outputs);

