sys_variable_value.value:
(function execute(inputs, outputs) {
    var regex = /(https:\/\/.+)(\/[a-f0-9]+\.jpg)/;
    var matches = inputs.avatarURL.match(regex);
    outputs.baseurl = matches[1];
    outputs.resourcepath = matches[2];
})(inputs, outputs);

