sys_variable_value.value:
(function execute(inputs, outputs) {

    var data = JSON.parse(inputs.response);
    var steamIds = data.friendslist.friends.map(friend => friend.steamid);
    outputs.steamids = steamIds;
    outputs.steamids_csv = steamIds.join(",");

})(inputs, outputs);

