sys_variable_value.value:
(function execute(inputs, outputs) {

    const extractBaseUrlAndImage = (url) => {
        // Regular expression to match the base URL and the image file name
        const regex = /^(https?:\/\/[^\/]+\/(?:.*\/)*)([^\/?]+)(\?.*)?$/;

        // Execute the regex on the URL
        const match = url.match(regex);

        if (match) {
            const baseUrl = match[1];
            const imageName = match[2];
            return { baseUrl: baseUrl, imageName : imageName };
        }

        return null;
    };

    const result = extractBaseUrlAndImage(inputs.url);
    const previewResult = extractBaseUrlAndImage(inputs.preview_url);
    
    outputs.result = result;
    outputs.preview_result = previewResult;

})(inputs, outputs);

