sys_variable_value.value:
(function execute(inputs, outputs) {
    // can't select sys_id of attachment record in pill picker for image field
    // convert sys_id to string so we can use in pill picker
    outputs.attachment_sysid = inputs.attachment_sysid + "";
    outputs.preview_attachment_sysid = inputs.preview_attachment_sysid + "";
})(inputs, outputs);

