sys_script_include.script:
var FaceOffExperiemental = Class.create();
FaceOffExperiemental.prototype = {
    initialize: function() {
    },

	getMyOpponentsWithCriterion: function() {

		var myGamesWithCriteria = [];
		var myCriteria = ['Epic','Great Soundtrack','Story Rich'];
		var encQry = this.makeGameWithCriterionRLQRY(myCriteria) + ''; 
		var myCompetitorGr = new GlideRecord('x_43553_faceoff_steam_game');
		myCompetitorGr.addEncodedQuery(encQry);
		var jQry = myCompetitorGr.addJoinQuery('x_43553_faceoff_opponent_judgement_eligibility', 'sys_id', 'opponent');
		jQry.addCondition('judge', FaceOffUtils.GetCurrentJudgeSysId());
		gs.info(myCompetitorGr.getEncodedQuery())
		myCompetitorGr.query();
		while(myCompetitorGr.next()){
			myGamesWithCriteria.push(myCompetitorGr.getDisplayValue());
		}

		return myGamesWithCriteria;
	},

	makeGameWithCriterionRLQRY: function(myCriteria) {
		var l = myCriteria.length;
		var startQry = `^RLQUERYx_43553_faceoff_opponent_criterion.opponent,>=${l}^criterion.name=`;
		var endQry = "^ENDRLQUERY";
		var qry = myCriteria.join("^ORcriterion.name=");

		return `${startQry}${qry}${endQry}`;
	},

    type: 'FaceOffExperiemental'
};
